# REGRESSION UTILITY FUNCTIONS

# Given how many models we need to create and present, we'll need some utility functions to help us make it manageable

# This function calculates Cohen's f2 effect size measure for cross-sectional linear models
# It can come in two forms:
#	1) Effect size of independent variables of one model on a depencent variable
#	2) Effect size of independent variables of one model on a dependent variable above and beyond a second model
# The first form is useful to get a basic effect size estimate of a model whereas
# the second form is useful to get a comparative estimate of increase in effect size between one model and another.
# The formula for 1) is: R^2(model) / (1 - R^2(model))
# The formula for 2) is: (R^2(Evaluated Model) - R^2(Compared Model) ) / (1 - R^2(Evaluated Model)), indicating the effect size of "Evaluated Model" above and beyond "Compared Model"

# The function can take one or two R^2 numeric parameters. If only one parameter is passed to it, formula 1 is used to calculate the f2 value
# If two parameters are passed to it, formula 2 is used to calculate the f2 value:

calculate_effect_size <- function(model_rsquared, comparison_rsquared) {
	if (missing(model_rsquared))
		stop("Model R^2 missing in call to function calculate_effect_size.\n");

	if (missing(comparison_rsquared)) {
		if (!is.numeric(model_rsquared))
			stop("Model R^2 value passed to function calculate_effect_size is not numeric.\n");

		f2_value <- model_rsquared / (1 - model_rsquared);
	}
	else {
		if (!is.numeric(model_rsquared) | !is.numeric(comparison_rsquared))
			stop("Model R^2 value or comparison R^2 value passed to function calculate_effect_size is/are not numeric.\n");

		f2_value <- (model_rsquared - comparison_rsquared) / (1 - model_rsquared);
	}
	return(f2_value);
}


# This function calculates effect size interpretations from Cohen's f2 value calculated in the previous function
# We create a character string that gives an interpretation of the effect size according to Cohen's categories
# where f2 values can be interpreted as: Small = 0.02; Medium = 0.15; Strong = 0.35.  We adjust this to a few more categories.
interpret_effect_size <- function(f2) {
	if (!is.numeric(f2)) {
		stop("f2 value passed to function interpret_effect_size is not numeric.\n");
	}
	if (f2 < 0.01)
		interpretation <- "Very small";

	if ((f2 >= 0.01) & (f2 < 0.04))
		interpretation <- "Small";

	if ((f2 >= 0.04) & (f2 < 0.10))
		interpretation <- "Small-to-medium";

	if ((f2 >= 0.10) & (f2 < 0.19))
		interpretation <- "Medium";

	if ((f2 >= 0.19) & (f2 < 0.28))
		interpretation <- "Medium-to-large";

	if ((f2 >= 0.28) & (f2 < 0.45))
		interpretation <- "Large";

	if (f2 >= 0.45)
		interpretation <- "Very large";

	return(interpretation);
}

# This function returns a character vector of stars corresponding to the level of significance of the p-value passed to it
# The levels used are "", "*", "**", "***", which are p > 0.05, p < 0.05, p < 0.01, p < 0.001 respectively
interpret_p_value <- function(p_value) {
	if (!is.numeric(p_value))
		stop("The p-value passed to function interpret_p_value is not numeric.\n");

	if (p_value >= 0.05)
		interpretation <- "";

	if ((p_value < 0.05) & (p_value >= 0.01))
		interpretation <- "*";

	if ((p_value < 0.01) & (p_value >= 0.001))
		interpretation <- "**";

	if ((p_value < 0.001))
		interpretation <- "***";

	return(interpretation);
}


# This function takes one or two linear models as argument and returns an S4 object containing many useful variables as follows:
# 	1) The original linear model passed to the function (as a superclass)
#	2) The heteroskedasticity-corrected coefficients, standard errors, t-values, and p-values for the independent variables in the linear model
#   3) The heteroskedasticity-corrected chi-squared value for the linear model
#   4) The R^2 value for the model - For linear models we use the traditional R^2 measures.
#	   For GLM, we use three different pseudo-R^2 measures and take the average
#      We choose this approach because no single R^2 measure is agreed upon for GLM, so averaging normalizes the variance in approaches
#	   The first measure, "McFadden pseudo R-squared" is based on McFadden, D. (1973). Conditional logit analysis of qualitative choice behavior. In Frontiers in Econometrics (Edited by P. Zarembka), 105-42. Academic Press, New York.
#	   The second measure, "Maximum likelihood pseudo R-squared" is based on Long, J. Scott. 1997. Regression Models for Categorical and Limited Dependent Variables. Sage. pp104-106.
# 	   The third measure, "Cragg & Uhler's pseudo R-squared" is based on Cragg, J. G. and Uhler, R. S. (1970). The demand for automobiles. Canad. J. Economics 3,386-406.
#	5) The f2 value for the model and its textual interpretation
#	6) The f2 value for the model relative to a comparative model and its textual interpretation (optional)

# First we define an S4 object class to contain these values, inheriting from class "lm" and adding the slots listed above
# Note that we allow the comparative slots to optionally be empty with the "ANY" class.

# Given that there are a lot of "count" type data that may lead to singularities in the linear regression models
# we use likelihood-ratio tests instead of Wald tests that can better handle data of that type.

mekki.lm <- setClass(Class="mekki.lm",
					 slots=c(coefs="numeric", stderrs="numeric", tvals="numeric", pvals="numeric",
							 lr_chi		   		= "numeric", 	lr_chi_p		= "numeric", lr_chi_df1		  = "numeric", lr_chi_df2="numeric", lr_chi_interpretation		      = "character", lr_chi_result_line		   = "character",
							 lr_chi_comparison 	= "numeric",	lr_p_comparison = "numeric", lr_df_comparison = "numeric", 						 lr_chi_comparison_interpretation = "character", lr_comparison_result_line = "character",

							 model_aic		   	= "numeric", 	model_aic_result_line 		= "character",
							 comparison_aic	  	= "numeric", 	comparison_aic_result_line	= "character",

							 r_squared="numeric", cohen_f2_model="numeric", cohen_f2_model_interpretation="character",
							 cohen_f2_comparison="ANY", cohen_f2_comparison_interpretation="ANY", cohen_model_line="character", cohen_comparison_line="character"
							),
					 contains="lm"
					);


# Then we define a second S4 object class to contain the values that are related to glm models, inheriting from class "GLM" and adding slightly different slots
# Note that we keep many slot names for alignment purposes even though they will be blank for glm tests
# The slot "tvals" is actually "zvals", but we leave it named as such for convenience
mekki.glm <- setClass(Class="mekki.glm",
					 slots=c(coefs="numeric", stderrs="numeric", tvals="numeric", pvals="numeric",
							 lr_chi		   	   = "numeric", 	lr_chi_p	 	= "numeric", lr_chi_df1		  = "numeric", lr_chi_df2 ="numeric", lr_chi_interpretation		   	   = "character", lr_chi_result_line		= "character",
							 lr_chi_comparison = "numeric", 	lr_p_comparison = "numeric", lr_df_comparison = "numeric", 						  lr_chi_comparison_interpretation = "character", lr_comparison_result_line = "character",

							 model_aic		   = "numeric", model_aic_result_line 		= "character",
							 comparison_aic	   = "numeric", comparison_aic_result_line	= "character",

							 r_squared="numeric", cohen_f2_model="numeric", cohen_f2_model_interpretation="character",
							 cohen_f2_comparison="ANY", cohen_f2_comparison_interpretation="ANY", cohen_model_line="character", cohen_comparison_line="character"
							),
					 contains="glm"
					);





# We add attributes to the models depending on type. Default is "lm".  Alternatives are "glm" and "betareg"

augment_model <- function(model, comparison, type="lm") {

	# Quick checks to ensure the parameters exists
	if(missing(model))
		stop("Model missing from call to function augment_model.\n");

	# Check type of model
	if (type=="lm") {
		if(missing(comparison)) {
			if (!("lm" %in% class(model)))
				stop("Model argument passed to function augment_model does not match the specified type. Default type is 'lm'.\n");

			# If the second parameter isn't supplied, then we set the f2 comparison values to NA
			cohen_f2_comparison_value 					<- NA;
			cohen_f2_comparison_interpretation_value	<- NA;
		}
		else {
			if (!("lm" %in% class(model)) | !("lm" %in% class(comparison)))
				stop("Model or comparison argument(s) passed to function augment_model do not (both) match the specified type. Default type is 'lm'.\n");

			# If the second parameter is supplied, create the comparison summary
			comparison_summary  						<- summary (comparison, vcov=hccm(model, type="hc3"));
			cohen_f2_comparison_value					<- "Calculate me";
			cohen_f2_comparison_interpretation_value	<- "Calculate me";
			comparison_lrtest 						<- lrtest(model, comparison);
			# lrtest puts a # in the name of the first column, so we rename it here to avoid problems later
			names(comparison_lrtest)[1] <- "Res.Df";
		}

		# Create the model's various tests
		model_coeftest 		<- coeftest(model, vcov=hccm(model, type="hc3"));
		model_lrtest_chi 	<- lrtest  (model);

		# lrtest puts a # in the name of the first column, so we rename it here to avoid problems later
		names(model_lrtest_chi)[1] <- "Res.Df";

		model_summary  		<- summary (model, vcov=hccm(model, type="hc3"));

		# Create the augmented model using the results from the model's various tests
		# If the comparison summary values exist, they'll be added.
		augmented_model <- mekki.lm(model,
									coefs								= model_coeftest[, 1],
									stderrs								= model_coeftest[, 2],
									tvals								= model_coeftest[, 3],
									pvals								= model_coeftest[, 4],
									lr_chi								= model_lrtest_chi$Chisq[2],
									lr_chi_interpretation				= interpret_p_value(model_lrtest_chi$"Pr(>Chisq)"[2]),
									lr_chi_p							= model_lrtest_chi$"Pr(>Chisq)"[2],
									lr_chi_df1							= abs(model_lrtest_chi$Df[2]),
									lr_chi_df2							= model_lrtest_chi$Res.Df[1],
									lr_chi_result_line					= paste(round(model_lrtest_chi$Chisq[2], 0), interpret_p_value(model_lrtest_chi$"Pr(>Chisq)"[2]), " (df = ", abs(model_lrtest_chi$Df[2]), "; ", model_lrtest_chi$Res.Df[1], ")", sep=""),
									lr_chi_comparison					= ifelse(is.na(cohen_f2_comparison_value), 0, comparison_lrtest$Chisq[2]),
									lr_chi_comparison_interpretation	= ifelse(is.na(cohen_f2_comparison_value), "", interpret_p_value(comparison_lrtest$"Pr(>Chisq)"[2])),
									lr_p_comparison						= ifelse(is.na(cohen_f2_comparison_value), 0, comparison_lrtest$"Pr(>Chisq)"[2]),
									lr_df_comparison					= ifelse(is.na(cohen_f2_comparison_value), 0, abs(comparison_lrtest$Df[2])),
									lr_comparison_result_line			= ifelse(is.na(cohen_f2_comparison_value), "", paste(formatC(round(comparison_lrtest$Chisq[2], 3), format="f", digits=3), interpret_p_value(comparison_lrtest$"Pr(>Chisq)"[2]), " (df = ", abs(comparison_lrtest$Df[2]), ")", sep="")),
									model_aic							= numeric(),
									model_aic_result_line				= "",
									comparison_aic						= numeric(),
									comparison_aic_result_line			= "",
									r_squared							= model_summary$r.squared,
									cohen_f2_model						= calculate_effect_size(model_summary$r.squared),
									cohen_f2_model_interpretation 		= interpret_effect_size(calculate_effect_size(model_summary$r.squared)),
									cohen_f2_comparison					= ifelse(is.na(cohen_f2_comparison_value), NA, calculate_effect_size(model_summary$r.squared, comparison_summary$r.squared)),
									cohen_f2_comparison_interpretation  = ifelse(is.na(cohen_f2_comparison_interpretation_value), NA, interpret_effect_size(calculate_effect_size(model_summary$r.squared, comparison_summary$r.squared))),
									cohen_model_line					= paste(formatC(round(calculate_effect_size(model_summary$r.squared), 3), format="f", digits=3), " (", interpret_effect_size(calculate_effect_size(model_summary$r.squared)), ")", sep=""),
									cohen_comparison_line				= paste(formatC(round(ifelse(is.na(cohen_f2_comparison_value), NA, calculate_effect_size(model_summary$r.squared, comparison_summary$r.squared)), 3), format="f", digits=3), " (",
																				ifelse(is.na(cohen_f2_comparison_interpretation_value), NA, interpret_effect_size(calculate_effect_size(model_summary$r.squared, comparison_summary$r.squared))), ")", sep="")
								);
	} # End of type "lm"
	else if (type=="glm") {

		if(missing(comparison)) {
			if (!("glm" %in% class(model)))
				stop("Model argument passed to function augment_model does not match the specified type. Default type is 'lm'.\n");

			# If the second parameter isn't supplied, then we set the f2 and AIC comparison values to NA
			cohen_f2_comparison_value 					<- NA;
			cohen_f2_comparison_interpretation_value	<- NA;
			comparison_aic_val							<- NA;
		}
		else {
			if (!("glm" %in% class(model)) | !("glm" %in% class(comparison)))
				stop("Model or comparison argument(s) passed to function augment_model do not (both) match the specified type. Default type is 'lm'.\n");

			# If the second parameter is supplied, create the comparison summary
			comparison_pR2								<- pR2(comparison);
			comparison_rsquared  						<- (as.numeric(comparison_pR2[4]) + as.numeric(comparison_pR2[5]) + as.numeric(comparison_pR2[6])) / 3;
			cohen_f2_comparison_value					<- "Calculate me";
			cohen_f2_comparison_interpretation_value	<- "Calculate me";
			comparison_aic_val 							<- AIC(comparison);
		}

		# Create the model's various tests
		model_coeftest 		<- coeftest(model,vcov=hccm(model, type="hc3"));
		model_aic_val		<- AIC(model);
		model_pR2			<- pR2(model);
		model_rsquared 		<- (as.numeric(model_pR2[4]) + as.numeric(model_pR2[5]) + as.numeric(model_pR2[6])) / 3;

		# Create the augmented model using the results from the model's various tests
		# If the comparison summary values exist, they'll be added.
		augmented_model <- mekki.glm(model,
									coefs								= model_coeftest[, 1],
									stderrs								= model_coeftest[, 2],
									tvals								= model_coeftest[, 3],	# Actually z vals, but similar, so naming kept for convenience
									pvals								= model_coeftest[, 4],  # p-vals of Z test, not t-test.
									lr_chi								= numeric(),
									lr_chi_interpretation				= "",
									lr_chi_p							= numeric(),
									lr_chi_df1							= numeric(),
									lr_chi_df2							= numeric(),
									lr_chi_result_line					= "",
									lr_chi_comparison					= numeric(),
									lr_chi_comparison_interpretation	= "",
									lr_p_comparison						= numeric(),
									lr_df_comparison					= numeric(),
									lr_comparison_result_line			= "",
									model_aic							= model_aic_val,
									model_aic_result_line				= as.character(round(model_aic_val, 0)),
									comparison_aic						= as.numeric(ifelse(is.na(comparison_aic_val), NA, round(comparison_aic_val, 0))),
									comparison_aic_result_line			= as.character(round(ifelse(is.na(comparison_aic_val), NA, akaike.weights(c(model_aic_val, comparison_aic_val))$deltaAIC[2]), 0)),
									r_squared							= model_rsquared,
									cohen_f2_model						= calculate_effect_size(model_rsquared),
									cohen_f2_model_interpretation 		= interpret_effect_size(calculate_effect_size(model_rsquared)),
									cohen_f2_comparison					= ifelse(is.na(cohen_f2_comparison_value), NA, calculate_effect_size(model_rsquared, comparison_rsquared)),
									cohen_f2_comparison_interpretation  = ifelse(is.na(cohen_f2_comparison_interpretation_value), NA, interpret_effect_size(calculate_effect_size(model_rsquared, comparison_rsquared))),
									cohen_model_line					= paste(formatC(round(calculate_effect_size(model_rsquared), 3), format="f", digits=3), " (", interpret_effect_size(calculate_effect_size(model_rsquared)), ")", sep=""),
									cohen_comparison_line				= paste(formatC(round(ifelse(is.na(cohen_f2_comparison_value), NA, calculate_effect_size(model_rsquared, comparison_rsquared)), 3), format="f", digits=3), " (",
																				ifelse(is.na(cohen_f2_comparison_interpretation_value), NA, interpret_effect_size(calculate_effect_size(model_rsquared, comparison_rsquared))), ")", sep="")
								);
	} # End of type "glm"
	else if (type=="betareg") {

		if(missing(comparison)) {
			if (!("betareg" %in% class(model)))
				stop("Model argument passed to function augment_model does not match the specified type. Default type is 'lm'.\n");

			# If the second parameter isn't supplied, then we set the f2 and AIC comparison values to NA
			cohen_f2_comparison_value 					<- NA;
			cohen_f2_comparison_interpretation_value	<- NA;
			comparison_aic_val							<- NA;
		}
		else {
			if (!("betareg" %in% class(model)) | !("betareg" %in% class(comparison)))
				stop("Model or comparison argument(s) passed to function augment_model do not (both) match the specified type. Default type is 'lm'.\n");

			# If the second parameter is supplied, create the comparison summary
			comparison_rsquared  						<- comparison$pseudo.r.squared;
			cohen_f2_comparison_value					<- "Calculate me";
			cohen_f2_comparison_interpretation_value	<- "Calculate me";
			comparison_aic_val 							<- AIC(comparison);
		}

		# Create the model's various tests
		model_coeftest 		<- betareg:::coeftest.betareg(model);
		model_aic_val		<- AIC(model);
		model_rsquared 		<- model$pseudo.r.squared;

		# Create the augmented model using the results from the model's various tests
		# If the comparison summary values exist, they'll be added.
		# Since none of the slot values change, we use the already-created mekki.glm container class

		# Since package betareg doesn't export its class definition for betareg, we need to instead trick the S4 constructor
		# into thinking it's just a standard glm class, which works fine for our needs.
		class(model) <- c("glm", "lm");

		augmented_model <- mekki.glm(model,
										coefs								= model_coeftest[, 1],
										stderrs								= model_coeftest[, 2],
										tvals								= model_coeftest[, 3],	# Actually z vals, but similar, so naming kept for convenience
										pvals								= model_coeftest[, 4],  # p-vals of Z test, not t-test.
										lr_chi								= numeric(),
										lr_chi_interpretation				= "",
										lr_chi_p							= numeric(),
										lr_chi_df1							= numeric(),
										lr_chi_df2							= numeric(),
										lr_chi_result_line					= "",
										lr_chi_comparison					= numeric(),
										lr_chi_comparison_interpretation	= "",
										lr_p_comparison						= numeric(),
										lr_df_comparison					= numeric(),
										lr_comparison_result_line			= "",
										model_aic							= model_aic_val,
										model_aic_result_line				= as.character(round(model_aic_val, 0)),
										comparison_aic						= as.numeric(ifelse(is.na(comparison_aic_val), NA, round(comparison_aic_val, 0))),
										comparison_aic_result_line			= as.character(round(ifelse(is.na(comparison_aic_val), NA, akaike.weights(c(model_aic_val, comparison_aic_val))$deltaAIC[2]), 0)),
										r_squared							= model_rsquared,
										cohen_f2_model						= calculate_effect_size(model_rsquared),
										cohen_f2_model_interpretation 		= interpret_effect_size(calculate_effect_size(model_rsquared)),
										cohen_f2_comparison					= ifelse(is.na(cohen_f2_comparison_value), NA, calculate_effect_size(model_rsquared, comparison_rsquared)),
										cohen_f2_comparison_interpretation  = ifelse(is.na(cohen_f2_comparison_interpretation_value), NA, interpret_effect_size(calculate_effect_size(model_rsquared, comparison_rsquared))),
										cohen_model_line					= paste(formatC(round(calculate_effect_size(model_rsquared), 3), format="f", digits=3), " (", interpret_effect_size(calculate_effect_size(model_rsquared)), ")", sep=""),
										cohen_comparison_line				= paste(formatC(round(ifelse(is.na(cohen_f2_comparison_value), NA, calculate_effect_size(model_rsquared, comparison_rsquared)), 3), format="f", digits=3), " (",
																					ifelse(is.na(cohen_f2_comparison_interpretation_value), NA, interpret_effect_size(calculate_effect_size(model_rsquared, comparison_rsquared))), ")", sep="")
								);
		augmented_model$loglik_result_line	<- paste(round(augmented_model$loglik, 0), " (df = ", augmented_model$df.residual, ")", sep="");
	} # End of type "betareg"
	else {
		stop("The type passed to function augment model must be one of 'lm' or 'glm' or 'betareg'\n");
	}

	return(augmented_model);
}


