												beta_percent_bugs_assigned_to_reassigned_at_least_once					= ( (percent_bugs_assigned_to_reassigned_at_least_once 					* (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2,
												beta_percent_bugs_assigned_to_defined_outcomes_fixed 					= ( (percent_bugs_assigned_to_defined_outcomes_fixed 					* (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2,
												beta_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch	= ( (percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch * (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2);

n_qa_contact_h2 			<- nrow(org_level_qa_contact_h2);
org_level_qa_contact_h2 	<<- org_level_qa_contact_h2 %>%
