# LOAD PACKAGES FROM LIBRARY
load_packages <- function () {

	# Load the Data.table package for data.table objects and fread() function
	library(data.table);

	# Stargazer does much of the organizing of results of the regression models into tables
	library(stargazer);

	# Package lmtest provides useful functions for comparing f-statistics of models
	# to determine which model is a better representation of underlying data
	library(lmtest);
	
	# Package lme4 provides functions for mixed-method nested regression models
	library(lme4);

	# Packages nortest, moments, and e1071 provide functions to test for assumption of normality in data

if (!FINAL_ONLY) {

	library(moments);
	library(nortest);
	library(e1071);
}

	# Package car has functions to adjust for heteroscedasticity resulting from linear models that assumed homoscedasticity
	# Citation: John Fox and Sanford Weisberg (2011). An {R} Companion to Applied Regression, Second Edition. Thousand Oaks CA: Sage. URL: http://socserv.socsci.mcmaster.ca/jfox/Books/Companion
	library(car);

	# Package pscl has functions to calculate pseudo-R^2 measures for Generalized Linear Models (GLMs)
	# Citation: Simon Jackman (2015). pscl: Classes and Methods for R Developed in the Political Science Computational Laboratory,
	#		    Stanford University. Department of Political Science, Stanford University. Stanford, California. R package version 1.4.9. URL http://pscl.stanford.edu/
	library(pscl);

	# Package qpcR has convenience functions to calculate and compare AICs for GLM models
	# Relevant citations for AIC & related comparison statistics: Akaike Information Criterion Statistics.Sakamoto Y, Ishiguro M and Kitagawa G.D. Reidel Publishing Company (1986).
	#															  Model selection and inference: a practical information-theoretic approach.Burnham KP & Anderson DR.Springer Verlag, New York, USA (2002).
	#															  AIC model selection using Akaike weights. Wagenmakers EJ & Farrell S.Psychonomic Bull Review (2004), 11: 192-196.
	library(qpcR);

	# Package betareg implements Beta Regression Model fitting which is more appropriate for dependent variables that are constrained between 0 & 1 such as rates or percentages
	# Details can be found as follows: (Cribari-Neto & Zeileis, 2010; Grün, Kosmidis, and Zeileis, 2012) built upon classic binomial probability statistical analysis principles (Williams, 1982; Prentice, 1986).
		# The analysis assumes that the dependent variable (percent fixed in our case) is beta-distributed, accounting for the heteroskedasticity & kurtosis inherent in standard unit interval data (as we observed in the graphs)
		# This approach has been found to be superior to log or power based transformations of the dependent variable  as it improves interpretability and accounts for non-Gaussian asymmetry (Ferrari & Cribari-Neto, 2004).
		# Further, it promotes data-driven statistical analysis rather than forcing standard tests to fit non-ideal data (Cribari-Neto & Zeileis, 2010)
		#
		# See also, Simas & Rocha (2006) and updated by Zeileis, et al. (2014)
		# Details provided here: https://cran.r-project.org/web/packages/betareg/vignettes/betareg.pdf & https://cran.r-project.org/web/packages/betareg/
		# Further details: Cribari-Neto & Zeileis (2010). Beta Regression in R. Journal of Statistical Software 34(2), 1-24. URL http://www.jstatsoft.org/v34/i02/.
		# And: Gruen, Kosmidis, & Zeileis (2012). Extended Beta Regression in R: Shaken, Stirred, Mixed, and Partitioned. Journal of Statistical Software, 48(11), 1-25. URL http://www.jstatsoft.org/v48/i11/.
	library(betareg);
	
	# The boot package contains the logit() function that we use to convert the percent values during mixed-effects modeling
	library(boot);
	
	# The RLRsim package provides likelihood ratio tests for and power and sensitivity estimates linear mixed models
		# The theory behind these tests was first developed in:
		# Crainiceanu, C. and Ruppert, D. (2004) Likelihood ratio tests in linear mixed models with one variance
		# component, Journal of the Royal Statistical Society: Series B, 66, 165–185.
		# Power analyses and sensitivity studies for RLRsim can be found in:
		# Scheipl, F., Greven, S. and Kuechenhoff, H. (2008) Size and power of tests for a zero random effect
		# variance or polynomial regression in additive and linear mixed models. Computational Statistics
		# and Data Analysis, 52(7), 3283–3299.
	library(RLRsim);
	
	# The piecewiseSEM package provides the rsquared() function to calculate marginal and conditional pseudo-r-squared values for linear mixed models and generalized linear mixed models (lmer & glmer)
	library(piecewiseSEM)
		
	# Dplyr package is used for all kinds of handy functions
	# Loaded last so that select() isn't masqued by dependencies of above functions (like package MASS)
	library(dplyr);
	
	# Dtplyr is the new split of package dplyr that allows manipulation of data.table objects the way older versions of dplyr used to
	# Loaded actually last to override dplyr
	library(dtplyr);

} # End load_packages function




load_utility_functions <- function() {


# REGRESSION UTILITY FUNCTIONS

# Given how many models we need to create and present, we'll need some utility functions to help us make it manageable

# This function calculates Cohen's f2 effect size measure for cross-sectional linear models
# It can come in two forms:
#	1) Effect size of independent variables of one model on a depencent variable
#	2) Effect size of independent variables of one model on a dependent variable above and beyond a second model
# The first form is useful to get a basic effect size estimate of a model whereas
# the second form is useful to get a comparative estimate of increase in effect size between one model and another.
# The formula for 1) is: R^2(model) / (1 - R^2(model))
# The formula for 2) is: (R^2(Evaluated Model) - R^2(Compared Model) ) / (1 - R^2(Evaluated Model)), indicating the effect size of "Evaluated Model" above and beyond "Compared Model"

# The function can take one or two R^2 numeric parameters. If only one parameter is passed to it, formula 1 is used to calculate the f2 value
# If two parameters are passed to it, formula 2 is used to calculate the f2 value:

calculate_effect_size <<- function(model_rsquared, comparison_rsquared) {
	if (missing(model_rsquared))
		stop("Model R^2 missing in call to function calculate_effect_size.\n");

	if (missing(comparison_rsquared)) {
		if (!is.numeric(model_rsquared))
			stop("Model R^2 value passed to function calculate_effect_size is not numeric.\n");

		f2_value <- model_rsquared / (1 - model_rsquared);
	}
	else {
		if (!is.numeric(model_rsquared) | !is.numeric(comparison_rsquared))
			stop("Model R^2 value or comparison R^2 value passed to function calculate_effect_size is/are not numeric.\n");

		f2_value <- (model_rsquared - comparison_rsquared) / (1 - model_rsquared);
	}
	return(f2_value);
}


# This function calculates effect size interpretations from Cohen's f2 value calculated in the previous function
# We create a character string that gives an interpretation of the effect size according to Cohen's categories
# where f2 values can be interpreted as: Small = 0.02; Medium = 0.15; Strong = 0.35.  We adjust this to a few more categories.
interpret_effect_size <<- function(f2) {
	if (!is.numeric(f2)) {
		stop("f2 value passed to function interpret_effect_size is not numeric.\n");
	}
	if (f2 < 0.01)
		interpretation <- "Very small";

	if ((f2 >= 0.01) & (f2 < 0.04))
		interpretation <- "Small";

	if ((f2 >= 0.04) & (f2 < 0.10))
		interpretation <- "Small-to-medium";

	if ((f2 >= 0.10) & (f2 < 0.19))
		interpretation <- "Medium";

	if ((f2 >= 0.19) & (f2 < 0.28))
		interpretation <- "Medium-to-large";

	if ((f2 >= 0.28) & (f2 < 0.45))
		interpretation <- "Large";

	if (f2 >= 0.45)
		interpretation <- "Very large";

	return(interpretation);
}

# This function returns a character vector of stars corresponding to the level of significance of the p-value passed to it
# The levels used are "", "*", "**", "***", which are p > 0.05, p < 0.05, p < 0.01, p < 0.001 respectively
interpret_p_value <<- function(p_value) {
	if (!is.numeric(p_value))
		stop("The p-value passed to function interpret_p_value is not numeric.\n");

	if (p_value >= 0.05)
		interpretation <- "";

	if ((p_value < 0.05) & (p_value >= 0.01))
		interpretation <- "*";

	if ((p_value < 0.01) & (p_value >= 0.001))
		interpretation <- "**";

	if ((p_value < 0.001))
		interpretation <- "***";

	return(interpretation);
}



# This function takes one or two linear models as argument and returns an S4 object containing many useful variables as follows:
# 	1) The original linear model passed to the function (as a superclass)
#	2) The heteroskedasticity-corrected coefficients, standard errors, t-values, and p-values for the independent variables in the linear model
#   3) The heteroskedasticity-corrected chi-squared value for the linear model
#   4) The R^2 value for the model - For linear models we use the traditional R^2 measures.
#	   For GLM, we use three different pseudo-R^2 measures and take the average
#      We choose this approach because no single R^2 measure is agreed upon for GLM, so averaging normalizes the variance in approaches
#	   The first measure, "McFadden pseudo R-squared" is based on McFadden, D. (1973). Conditional logit analysis of qualitative choice behavior. In Frontiers in Econometrics (Edited by P. Zarembka), 105-42. Academic Press, New York.
#	   The second measure, "Maximum likelihood pseudo R-squared" is based on Long, J. Scott. 1997. Regression Models for Categorical and Limited Dependent Variables. Sage. pp104-106.
# 	   The third measure, "Cragg & Uhler's pseudo R-squared" is based on Cragg, J. G. and Uhler, R. S. (1970). The demand for automobiles. Canad. J. Economics 3,386-406.
#	5) The f2 value for the model and its textual interpretation
#	6) The f2 value for the model relative to a comparative model and its textual interpretation (optional)

# First we define an S4 object class to contain these values, inheriting from class "lm" and adding the slots listed above


mekki.lm <<- setClass(Class="mekki.lm",
					 slots=c(model_aic		   	= "numeric", 	model_aic_result_line 		= "character",
							 model_bic		   	= "numeric", 	model_bic_result_line 		= "character",

							 r_squared			= "numeric"
							),
					 contains="lm"
					);


# Then we define a second S4 object class to contain the values that are related to glm models, inheriting from class "GLM" and adding slightly different slots

mekki.glm <<- setClass(Class="mekki.glm",
					 slots=c(model_aic		   	= "numeric", 	model_aic_result_line 		= "character",
							 model_bic		   	= "numeric", 	model_bic_result_line 		= "character",

							 r_squared			= "numeric"
							),
					 contains="glm"
					);


					
# We create similar reduced versions for mixed models classes lmer(Mod) and glmer(Mod)					

mekki.lmer <<- setClass(Class="mekki.lmer",
					 slots=c(model_aic		   	= "numeric", 	model_aic_result_line 		= "character",
							 comparison_aic	  	= "numeric", 	comparison_aic_result_line	= "character",
							 
							 model_bic		   	= "numeric", 	model_bic_result_line 		= "character",
							 comparison_bic	  	= "numeric", 	comparison_bic_result_line	= "character",

							 r_squared_marginal	= "numeric", 	r_squared_conditional		= "numeric",
							 
							 cohen_f2_comparison = "ANY",		cohen_f2_comparison_interpretation = "ANY", cohen_comparison_line = "character"
							),
					 contains="lmerMod"
					);					
					
					
					
mekki.glmer <<- setClass(Class="mekki.glmer",
					 slots=c(model_aic		   	= "numeric", 	model_aic_result_line 		= "character",
							 comparison_aic	  	= "numeric", 	comparison_aic_result_line	= "character",
							 
							 model_bic		   	= "numeric", 	model_bic_result_line 		= "character",
							 comparison_bic	  	= "numeric", 	comparison_bic_result_line	= "character",

							 r_squared_marginal	= "numeric", 	r_squared_conditional		= "numeric",
							 
							 cohen_f2_comparison = "ANY",		cohen_f2_comparison_interpretation = "ANY", cohen_comparison_line = "character"
							),
					 contains="glmerMod"
					);

					

# We add attributes to the models depending on type. Default is "lmer".  Alternatives are "lglm", "glm", and "glmer"

augment_model <<- function(model, comparison, type="lmer") {

	# Quick checks to ensure the parameters exists
	if(missing(model))
		stop("Model missing from call to function augment_model.\n");

	# Check type of model
	if (type=="lm") {
		
		if (!("lm" %in% class(model)))
			stop("Model argument passed to function augment_model does not match specified type 'lm'.\n");

		# Create the model's various tests
		model_aic_val		= AIC(model);
		model_bic_val		= BIC(model);
		model_rsquared	 	= as.numeric(rsquared(model)[5]);

		# Create the augmented model using the results from the model's various tests
		# If the comparison summary values exist, they'll be added.
		augmented_model <- mekki.lm(model,
									model_aic									= model_aic_val,
									model_aic_result_line						= as.character(round(model_aic_val, 0)),
									
									model_bic									= model_bic_val,
									model_bic_result_line						= as.character(round(model_bic_val, 0)),
		
									r_squared									= model_rsquared
								);
	} # End of type "lm"
	else if (type=="glm") {

		if (!("glm" %in% class(model)))
			stop("Model argument passed to function augment_model does not match the specified type 'glm'.\n");

		# Create the model's various tests
		model_aic_val		= AIC(model);
		model_bic_val		= BIC(model);
		model_rsquared	 	= as.numeric(rsquared(model)[5]);
		
		# Create the augmented model using the results from the model's various tests
		# If the comparison summary values exist, they'll be added.
		augmented_model <- mekki.glm(model,
									 model_aic									= model_aic_val,
									 model_aic_result_line						= as.character(round(model_aic_val, 0)),
									
									 model_bic									= model_bic_val,
									 model_bic_result_line						= as.character(round(model_bic_val, 0)),
		
									 r_squared									= model_rsquared
								);
	} # End of type "glm"
	else if (type=="lmer") {

		if(missing(comparison)) {
				stop("Comparison model is missing in call to augment_model for type 'lmer'.\n");
		}
		else {
			if (!("lmerMod" %in% class(model)) | !("lm" %in% class(comparison)))
				stop("Model or comparison argument(s) passed to function augment_model do not (both) match the specified type 'lmer'.\n");

			# Create the comparison summary values
			comparison_aic_val 							<- AIC(comparison);
			comparison_bic_val 							<- BIC(comparison);
		}

		# Create the model's various tests
		model_aic_val							<- AIC(model);
		model_bic_val							<- BIC(model);
		model_rsquared_marginal 				<- tryCatch({as.numeric(rsquared(model)[5])}, error = function(err) {return(as.numeric(0))});
		model_rsquared_conditional 				<- tryCatch({as.numeric(rsquared(model)[6])}, error = function(err) {return(as.numeric(0))});
		cohen_f2_comparison_val					<- calculate_effect_size(model_rsquared_conditional, model_rsquared_marginal);
		cohen_f2_comparison_interpretation_val  <- interpret_effect_size(cohen_f2_comparison_val); 		

		# Create the augmented model using the results from the model's various tests
		augmented_model <- mekki.lmer(model,
									  model_aic									= model_aic_val,
									  model_aic_result_line						= as.character(round(model_aic_val, 0)),
									  comparison_aic							= as.numeric(round(comparison_aic_val, 0)),
									  comparison_aic_result_line				= as.character(round(akaike.weights(c(model_aic_val, comparison_aic_val))$deltaAIC[2]), 0),
									  		
									  model_bic									= model_bic_val,
									  model_bic_result_line						= as.character(round(model_bic_val, 0)),
									  comparison_bic							= as.numeric(round(comparison_bic_val, 0)),
									  comparison_bic_result_line				= as.character(round(akaike.weights(c(model_bic_val, comparison_bic_val))$deltaAIC[2]), 0),
									  		
									  r_squared_marginal						= model_rsquared_marginal,
									  r_squared_conditional						= model_rsquared_conditional,
									  
									  cohen_f2_comparison						= cohen_f2_comparison_val,
									  cohen_f2_comparison_interpretation  		= cohen_f2_comparison_interpretation_val,
									  
									  cohen_comparison_line						= paste(formatC(round(cohen_f2_comparison_val, 3), format="f", digits=3), " (", cohen_f2_comparison_interpretation_val, ")", sep="")

								);
	} # End of type "lmer"
	else if (type=="glmer") {

		if(missing(comparison)) {
				stop("Comparison model is missing in call to augment_model for type 'glmer'.\n");
		}
		else {
			if (!("glmerMod" %in% class(model)) | !("glm" %in% class(comparison)))
				stop("Model or comparison argument(s) passed to function augment_model do not (both) match the specified type 'glmer'.\n");

			# Create the comparison summary values
			comparison_aic_val 							<- AIC(comparison);
			comparison_bic_val 							<- BIC(comparison);
		}

		# Create the model's various tests
		model_aic_val							<- AIC(model);
		model_bic_val							<- BIC(model);
		model_rsquared_marginal 				<- tryCatch({as.numeric(rsquared(model)[5] / 2)}, error = function(err) {return(as.numeric(0))});
		model_rsquared_conditional 				<- tryCatch({as.numeric(rsquared(model)[6] / 2)}, error = function(err) {return(as.numeric(0))});
		cohen_f2_comparison_val					<- calculate_effect_size(model_rsquared_conditional, model_rsquared_marginal);
		cohen_f2_comparison_interpretation_val  <- interpret_effect_size(cohen_f2_comparison_val); 		

		# Create the augmented model using the results from the model's various tests
		augmented_model <- mekki.glmer(model,
									   model_aic									= model_aic_val,
									   model_aic_result_line						= as.character(round(model_aic_val, 0)),
									   comparison_aic								= as.numeric(round(comparison_aic_val, 0)),
									   comparison_aic_result_line					= as.character(round(akaike.weights(c(model_aic_val, comparison_aic_val))$deltaAIC[2]), 0),
									   		
									   model_bic									= model_bic_val,
									   model_bic_result_line						= as.character(round(model_bic_val, 0)),
									   comparison_bic								= as.numeric(round(comparison_bic_val, 0)),
									   comparison_bic_result_line					= as.character(round(akaike.weights(c(model_bic_val, comparison_bic_val))$deltaAIC[2]), 0),
									   		
									   r_squared_marginal							= model_rsquared_marginal,
									   r_squared_conditional						= model_rsquared_conditional,
									   
									   cohen_f2_comparison							= cohen_f2_comparison_val,
									   cohen_f2_comparison_interpretation  			= cohen_f2_comparison_interpretation_val,
									   
									   cohen_comparison_line						= paste(formatC(round(cohen_f2_comparison_val, 3), format="f", digits=3), " (", cohen_f2_comparison_interpretation_val, ")", sep="")

								);
	} # End of type "glmer"
	else {
		stop("The type passed to function augment model must be one of 'lm' or 'glm' or 'lmer' or 'glmer'\n");
	}

	return(augmented_model);
}


} # End function load_utility_functions


