# PREPARE DATA

# This function creates slightly modified versions of columns for specific analyses
# such as logical threshold variables of continuous base variables
# The need for these additional or modified columns is discussed in the functions below

prep_data <- function () {

# Import profile_level table to work on;
profiles_working <- profile_level;

# Create logical variables based on criteria discussed in sections below
profiles_working <- mutate(profiles_working,
					bugs_reported_at_least_one_reopened 											= as.logical(bugs_reported_reopened_count 										> 0),
					bugs_assigned_to_at_least_one_reopened 											= as.logical(bugs_assigned_to_reopened_count 									> 0),
					bugs_qa_contact_at_least_one_reopened											= as.logical(bugs_qa_contact_reopened_count 									> 0),

					bugs_reported_at_least_one_reassigned											= as.logical(bugs_reported_reassigned_count 									> 0),
					bugs_assigned_to_at_least_one_reassigned										= as.logical(bugs_assigned_to_reassigned_count									> 0),
					bugs_qa_contact_at_least_one_reassigned											= as.logical(bugs_qa_contact_reassigned_count									> 0),

					percent_bugs_reported_defined_outcomes_fixed_at_least_one						= as.logical(percent_bugs_reported_defined_outcomes_fixed						> 0),
