# CODIFIABILITY ANALYSIS

codifiability_analysis <- function () {

# This function runs statistical tests to analyze the hypotheses
# related to codifiability.  Its independent variables are as follows:
#
# Reporter role:
#	log_bugs_reported_all_types_description_mean_length
#	bugs_reported_description_readability_Flesch_reading_ease_mean
#	log_bugs_reported_attachments_all_types_mean
#	percent_bugs_reported_is_duplicate
#	percent_bugs_reported_was_duplicated
#	log_bugs_reported_all_types_comments_mean_length
#	log_bugs_reported_comments_all_actors_mean
#
# Assigned_to role:
#	log_bugs_assigned_to_all_types_description_mean_length
#	bugs_assigned_to_description_readability_Flesch_reading_ease_mean
#	log_bugs_assigned_to_attachments_all_types_mean
#	log_bugs_assigned_to_all_types_comments_mean_length
#	log_bugs_assigned_to_comments_all_actors_mean
#
# Independent variables for qa_contact role:
#	log_bugs_qa_contact_all_types_description_mean_length
#	bugs_qa_contact_description_readability_Flesch_reading_ease_mean
#	log_bugs_qa_contact_attachments_all_types_mean
#	log_bugs_qa_contact_all_types_comments_mean_length
#	log_bugs_qa_contact_comments_all_actors_mean




# Data imputation:

# Because of missing data for some of the IV's, in order to do the comparison tests correctly, the base models
# need to be re estimated with missing data rows removed.

# We create an H2 specific subset of profile_level and its related subsets with omitted cases removed to ensure proper fitting and comparison

# At the profile level all dependent variables (and many independent variables) are separated by role in which the profile acted on collections of bugs
# We handle each of the three main roles, reporter, assigned_to, qa_contact, in order in both base and full model design sections below

profile_level_reporter_h2 <<- na.omit(as.data.frame(profile_level_reporter)[ , c(

																				# Random effects classificaiton variable
																				"domain",

																				# Dependent variables for reporter role
																				"bugs_reported_at_least_one_reopened",
																				"percent_bugs_reported_reopened_at_least_once",
																				"bugs_reported_at_least_one_reassigned",
																				"percent_bugs_reported_reassigned_at_least_once",
																				"percent_bugs_reported_defined_outcomes_fixed",
																				"percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch",
																				"log_bugs_reported_all_types_mean_days_to_resolution",

																				# Control variables for reporter role
																				"is_core_actor",
																				"log_activity_count",
																				"log_bugs_reported_count",
																				# "log_bugs_reported_all_types_mean_days_to_resolution",	-> Is DV so included above
																				# "percent_bugs_reported_is_duplicate",						-> Is IV for H2 so included below
																				"percent_bugs_reported_defined_outcomes_fixed",
																				"percent_bugs_reported_violated_bug_lifecycle",

																				# Independent variables for reporter role
																				"log_bugs_reported_all_types_description_mean_length",
																				"bugs_reported_description_readability_Flesch_reading_ease_mean",
																				"log_bugs_reported_attachments_all_types_mean",
																				"percent_bugs_reported_is_duplicate",
																				"percent_bugs_reported_was_duplicated",
																				"log_bugs_reported_all_types_comments_mean_length",
																				"log_bugs_reported_comments_all_actors_mean"

																		)]);


profile_level_assigned_to_h2 <<- na.omit(as.data.frame(profile_level_assigned_to)[ , c(	

																						# Random effects classificaiton variable
																						"domain",
																					
																						# Dependent variables for assigned_to role
																						"bugs_assigned_to_at_least_one_reopened",
																						"percent_bugs_assigned_to_reopened_at_least_once",
																						"bugs_assigned_to_at_least_one_reassigned",
																						"percent_bugs_assigned_to_reassigned_at_least_once",
																						"percent_bugs_assigned_to_defined_outcomes_fixed",
																						"percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch",
																						"log_bugs_assigned_to_all_types_mean_days_to_resolution",

																						# Control variables for assigned_to role
																						"is_core_actor",
																						"log_activity_count",
																						"log_bugs_assigned_to_count",
																						# "log_bugs_assigned_to_all_types_mean_days_to_resolution", -> Is DV so included above
																						"percent_bugs_assigned_to_defined_outcomes_fixed",
																						"percent_bugs_assigned_to_violated_bug_lifecycle",

																						# Independent variables for assigned_to role
																						"log_bugs_assigned_to_all_types_description_mean_length",
																						"bugs_assigned_to_description_readability_Flesch_reading_ease_mean",
																						"log_bugs_assigned_to_attachments_all_types_mean",
																						"log_bugs_assigned_to_all_types_comments_mean_length",
																						"log_bugs_assigned_to_comments_all_actors_mean"
																		)]);


profile_level_qa_contact_h2 <<- na.omit(as.data.frame(profile_level_qa_contact)[ , c(

																					# Random effects classificaiton variable
																					"domain",
																					
																					# Dependent variables for qa_contact role
																					"bugs_qa_contact_at_least_one_reopened",
																					"percent_bugs_qa_contact_reopened_at_least_once",
																					"bugs_qa_contact_at_least_one_reassigned",
																					"percent_bugs_qa_contact_reassigned_at_least_once",
																					"percent_bugs_qa_contact_defined_outcomes_fixed",
																					"percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch",
																					"log_bugs_qa_contact_all_types_mean_days_to_resolution",

																					# Control variables for qa_contact role
																					"is_core_actor",
																					"log_activity_count",
																					"log_bugs_qa_contact_count",
																					# "log_bugs_qa_contact_all_types_mean_days_to_resolution", 	-> Is DV so included above
																					"percent_bugs_qa_contact_defined_outcomes_fixed",
																					"percent_bugs_qa_contact_violated_bug_lifecycle",

																					# Independent variables for qa_contact role
																					"log_bugs_qa_contact_all_types_description_mean_length",
																					"bugs_qa_contact_description_readability_Flesch_reading_ease_mean",
																					"log_bugs_qa_contact_attachments_all_types_mean",
																					"log_bugs_qa_contact_all_types_comments_mean_length",
																					"log_bugs_qa_contact_comments_all_actors_mean"
																		)]);



# Before we con create the control models, given that our percent dependent variables include the extremes of 0% and 100% in some cases,
# in order to do beta regression we must first transform those DVs such that:  (y * (n - 1) + 0.5)/n where n is the sample size (Smithson and Verkuilen 2006).
# Details on why this is a valid transformation in practice for beta regression can be found here:
#	Smithson M, Verkuilen J (2006). “A Better Lemon Squeezer? Maximum-Likelihood Regression with Beta-Distributed Dependent Variables.” Psychological Methods, 11(1), 54–71.

# Since we don't know the n values until the omited cases have been removed, this transformation must be done separately for each role in each hypothesis test section.

n_reporter_h2 			  		<- nrow(profile_level_reporter_h2);
profile_level_reporter_h2 		<<- profile_level_reporter_h2 %>%
										mutate(	beta_percent_bugs_reported_reopened_at_least_once						= ( (percent_bugs_reported_reopened_at_least_once 						* (n_reporter_h2 - 1)) + 0.5 ) / n_reporter_h2,
												beta_percent_bugs_reported_reassigned_at_least_once						= ( (percent_bugs_reported_reassigned_at_least_once 					* (n_reporter_h2 - 1)) + 0.5 ) / n_reporter_h2,
												beta_percent_bugs_reported_defined_outcomes_fixed 						= ( (percent_bugs_reported_defined_outcomes_fixed 						* (n_reporter_h2 - 1)) + 0.5 ) / n_reporter_h2,
												beta_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch 	= ( (percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch 	* (n_reporter_h2 - 1)) + 0.5 ) / n_reporter_h2);

n_assigned_to_h2 			 	<- nrow(profile_level_assigned_to_h2);
profile_level_assigned_to_h2 	<<- profile_level_assigned_to_h2 %>%
										mutate(	beta_percent_bugs_assigned_to_reopened_at_least_once					= ( (percent_bugs_assigned_to_reopened_at_least_once 					* (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2,
												beta_percent_bugs_assigned_to_reassigned_at_least_once					= ( (percent_bugs_assigned_to_reassigned_at_least_once 					* (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2,
												beta_percent_bugs_assigned_to_defined_outcomes_fixed 					= ( (percent_bugs_assigned_to_defined_outcomes_fixed 					* (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2,
												beta_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch	= ( (percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch * (n_assigned_to_h2 - 1)) + 0.5 ) / n_assigned_to_h2);

n_qa_contact_h2 				<- nrow(profile_level_qa_contact_h2);
profile_level_qa_contact_h2 	<<- profile_level_qa_contact_h2 %>%
										mutate(	beta_percent_bugs_qa_contact_reopened_at_least_once						= ( (percent_bugs_qa_contact_reopened_at_least_once 					* (n_qa_contact_h2 - 1)) + 0.5 ) / n_qa_contact_h2,
												beta_percent_bugs_qa_contact_reassigned_at_least_once					= ( (percent_bugs_qa_contact_reassigned_at_least_once 					* (n_qa_contact_h2 - 1)) + 0.5 ) / n_qa_contact_h2,
												beta_percent_bugs_qa_contact_defined_outcomes_fixed 					= ( (percent_bugs_qa_contact_defined_outcomes_fixed 					* (n_qa_contact_h2 - 1)) + 0.5 ) / n_qa_contact_h2,
												beta_percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch 	= ( (percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch 	* (n_qa_contact_h2 - 1)) + 0.5 ) / n_qa_contact_h2);

												
												
# In order to add random effects, linear modeling is necessary as beta regression random effects isn't yet implemented in any R packages
# As a result, we transform the beta_percent values to logistic proportions using the logit() function in the boot package
# While the result will not be as clean as the beta regresions at the profile-level, they will be sufficient for comparing the additive benefit
# of the random effects for domain, which is our triangulation goal in this portion of the analysis											
												

profile_level_reporter_h2 		<<- profile_level_reporter_h2 %>%
										mutate(	logit_percent_bugs_reported_reopened_at_least_once							= logit(beta_percent_bugs_reported_reopened_at_least_once),						
												logit_percent_bugs_reported_reassigned_at_least_once						= logit(beta_percent_bugs_reported_reassigned_at_least_once),						
												logit_percent_bugs_reported_defined_outcomes_fixed 							= logit(beta_percent_bugs_reported_defined_outcomes_fixed), 						
												logit_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch 		= logit(beta_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch)); 	
    
profile_level_assigned_to_h2 	<<- profile_level_assigned_to_h2 %>%  
										mutate(	logit_percent_bugs_assigned_to_reopened_at_least_once						= logit(beta_percent_bugs_assigned_to_reopened_at_least_once),					
												logit_percent_bugs_assigned_to_reassigned_at_least_once						= logit(beta_percent_bugs_assigned_to_reassigned_at_least_once),					
												logit_percent_bugs_assigned_to_defined_outcomes_fixed 						= logit(beta_percent_bugs_assigned_to_defined_outcomes_fixed), 					
												logit_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch	= logit(beta_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch));	
    
profile_level_qa_contact_h2 	<<- profile_level_qa_contact_h2 %>%
										mutate(	logit_percent_bugs_qa_contact_reopened_at_least_once						= logit(beta_percent_bugs_qa_contact_reopened_at_least_once),						
												logit_percent_bugs_qa_contact_reassigned_at_least_once						= logit(beta_percent_bugs_qa_contact_reassigned_at_least_once),					
												logit_percent_bugs_qa_contact_defined_outcomes_fixed 						= logit(beta_percent_bugs_qa_contact_defined_outcomes_fixed), 					
												logit_percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch		= logit(beta_percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch)); 	
												
												

# Now that the hypothesis specific contraints have been applied, we
# create subsets of each role where at least one bug was reopened for those DVs that depend on it

profile_level_reporter_reopened_h2			<<- filter(profile_level_reporter_h2, 		bugs_reported_at_least_one_reopened);
profile_level_assigned_to_reopened_h2		<<- filter(profile_level_assigned_to_h2, 	bugs_assigned_to_at_least_one_reopened);
profile_level_qa_contact_reopened_h2		<<- filter(profile_level_qa_contact_h2, 	bugs_qa_contact_at_least_one_reopened);


# We reepat for subsets where at least one bug was reassigned
profile_level_reporter_reassigned_h2		<<- filter(profile_level_reporter_h2, 		bugs_reported_at_least_one_reassigned);
profile_level_assigned_to_reassigned_h2		<<- filter(profile_level_assigned_to_h2, 	bugs_assigned_to_at_least_one_reassigned);
profile_level_qa_contact_reassigned_h2		<<- filter(profile_level_qa_contact_h2, 	bugs_qa_contact_at_least_one_reassigned);




# MODELING


# Random effects hierarchical regression modeling of strongest single independent variable predictor
# For the purpose of evaluating the degree to which profile-level effects are hierarchically nested in organization-level effects
# we consider the random effects of "domain" in a mixed model on the intercept and the strongest single independent predictor for each base model.
# We then compare the base model and random effects model via ANOVA to determine if random effects modeling is superior

# Below are the base models with the single highest likelihood non-control independent variable for each profile-level model for each role

#
# Reporter role:
#

# Dependent variable log_bugs_reported_all_types_mean_days_to_resolution

h2_log_bugs_reported_all_types_mean_days_to_resolution_base_lm_model <<-  lm(log_bugs_reported_all_types_mean_days_to_resolution ~ 	1 +	log_bugs_reported_all_types_description_mean_length					+
																																		bugs_reported_description_readability_Flesch_reading_ease_mean		+
