# Dependent variable bugs_reported_at_least_one_reassigned:

h2_bugs_reported_at_least_one_reassigned_base_glm_model	<<- glm(bugs_reported_at_least_one_reassigned ~ 1 + log_bugs_reported_attachments_all_types_mean						+
