																											log_bugs_reported_all_types_comments_mean_length					+
																											log_bugs_reported_comments_all_actors_mean,
																											
																	data=profile_level_reporter_h2, family = binomial(link = "logit"));



# Dependent variable logit_percent_bugs_reported_reassigned_at_least_once:

h2_logit_percent_bugs_reported_reassigned_at_least_once_base_lm_model <<- lm(logit_percent_bugs_reported_reassigned_at_least_once ~ 1 + log_bugs_reported_attachments_all_types_mean						+
																																		percent_bugs_reported_was_duplicated								+
																																		log_bugs_reported_all_types_comments_mean_length					+
																																		log_bugs_reported_comments_all_actors_mean,


																					data=profile_level_reporter_reassigned_h2);



# Dependent variable logit_percent_bugs_reported_defined_outcomes_fixed

h2_logit_percent_bugs_reported_defined_outcomes_fixed_base_lm_model <<- lm(logit_percent_bugs_reported_defined_outcomes_fixed ~ 	1 + log_bugs_reported_all_types_description_mean_length					+
																																		bugs_reported_description_readability_Flesch_reading_ease_mean		+
																																		log_bugs_reported_attachments_all_types_mean						+
																																		percent_bugs_reported_is_duplicate									+
																																		percent_bugs_reported_was_duplicated								+
																																		log_bugs_reported_all_types_comments_mean_length					+
																																		log_bugs_reported_comments_all_actors_mean,
																																		
																				data=profile_level_reporter_h2);



# Dependent variable logit_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch

h2_logit_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch_base_lm_model <<- lm(logit_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch ~ 	1 + bugs_reported_description_readability_Flesch_reading_ease_mean		+
																																											log_bugs_reported_attachments_all_types_mean						+
																																											percent_bugs_reported_is_duplicate									+
																																											percent_bugs_reported_was_duplicated								+
