												logit_percent_bugs_assigned_to_defined_outcomes_fixed 						= logit(beta_percent_bugs_assigned_to_defined_outcomes_fixed), 					
												logit_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch	= logit(beta_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch));	
    
