																					"log_bugs_qa_contact_activity_3_7days_mean",
																					"log_bugs_qa_contact_activity_7_15days_mean",
																					"log_bugs_qa_contact_activity_15_45days_mean",
																					"log_bugs_qa_contact_activity_45_90days_mean",
																					"log_bugs_qa_contact_activity_90_180days_mean",
																					"log_bugs_qa_contact_activity_180days_1year_mean"
																		)]);



# Before we con create the control models, given that our percent dependent variables include the extremes of 0% and 100% in some cases,
# in order to do beta regression we must first transform those DVs such that:  (y * (n - 1) + 0.5)/n where n is the sample size (Smithson and Verkuilen 2006).
# Details on why this is a valid transformation in practice for beta regression can be found here:
#	Smithson M, Verkuilen J (2006). “A Better Lemon Squeezer? Maximum-Likelihood Regression with Beta-Distributed Dependent Variables.” Psychological Methods, 11(1), 54–71.

# Since we don't know the n values until the omited cases have been removed, this transformation must be done separately for each role in each hypothesis test section.

n_reporter_h4 			  		<- nrow(profile_level_reporter_h4);
profile_level_reporter_h4 		<<- profile_level_reporter_h4 %>%
										mutate(	beta_percent_bugs_reported_reopened_at_least_once						= ( (percent_bugs_reported_reopened_at_least_once 						* (n_reporter_h4 - 1)) + 0.5 ) / n_reporter_h4,
												beta_percent_bugs_reported_reassigned_at_least_once						= ( (percent_bugs_reported_reassigned_at_least_once 					* (n_reporter_h4 - 1)) + 0.5 ) / n_reporter_h4,
												beta_percent_bugs_reported_defined_outcomes_fixed 						= ( (percent_bugs_reported_defined_outcomes_fixed 						* (n_reporter_h4 - 1)) + 0.5 ) / n_reporter_h4,
												beta_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch 	= ( (percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch 	* (n_reporter_h4 - 1)) + 0.5 ) / n_reporter_h4);

n_assigned_to_h4 			 	<- nrow(profile_level_assigned_to_h4);
profile_level_assigned_to_h4 	<<- profile_level_assigned_to_h4 %>%
										mutate(	beta_percent_bugs_assigned_to_reopened_at_least_once					= ( (percent_bugs_assigned_to_reopened_at_least_once 					* (n_assigned_to_h4 - 1)) + 0.5 ) / n_assigned_to_h4,
												beta_percent_bugs_assigned_to_reassigned_at_least_once					= ( (percent_bugs_assigned_to_reassigned_at_least_once 					* (n_assigned_to_h4 - 1)) + 0.5 ) / n_assigned_to_h4,
												beta_percent_bugs_assigned_to_defined_outcomes_fixed 					= ( (percent_bugs_assigned_to_defined_outcomes_fixed 					* (n_assigned_to_h4 - 1)) + 0.5 ) / n_assigned_to_h4,
												beta_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch	= ( (percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch * (n_assigned_to_h4 - 1)) + 0.5 ) / n_assigned_to_h4);

n_qa_contact_h4 				<- nrow(profile_level_qa_contact_h4);
profile_level_qa_contact_h4 	<<- profile_level_qa_contact_h4 %>%
										mutate(	beta_percent_bugs_qa_contact_reopened_at_least_once						= ( (percent_bugs_qa_contact_reopened_at_least_once 					* (n_qa_contact_h4 - 1)) + 0.5 ) / n_qa_contact_h4,
												beta_percent_bugs_qa_contact_reassigned_at_least_once					= ( (percent_bugs_qa_contact_reassigned_at_least_once 					* (n_qa_contact_h4 - 1)) + 0.5 ) / n_qa_contact_h4,
												beta_percent_bugs_qa_contact_defined_outcomes_fixed 					= ( (percent_bugs_qa_contact_defined_outcomes_fixed 					* (n_qa_contact_h4 - 1)) + 0.5 ) / n_qa_contact_h4,
												beta_percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch 	= ( (percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch 	* (n_qa_contact_h4 - 1)) + 0.5 ) / n_qa_contact_h4);


												
												
# In order to add random effects, linear modeling is necessary as beta regression random effects isn't yet implemented in any R packages
# As a result, we transform the beta_percent values to logistic proportions using the logit() function in the boot package
# While the result will not be as clean as the beta regresions at the profile-level, they will be sufficient for comparing the additive benefit
# of the random effects for domain, which is our triangulation goal in this portion of the analysis											
												

profile_level_reporter_h4 		<<- profile_level_reporter_h4 %>%
										mutate(	logit_percent_bugs_reported_reopened_at_least_once							= logit(beta_percent_bugs_reported_reopened_at_least_once),						
												logit_percent_bugs_reported_reassigned_at_least_once						= logit(beta_percent_bugs_reported_reassigned_at_least_once),						
												logit_percent_bugs_reported_defined_outcomes_fixed 							= logit(beta_percent_bugs_reported_defined_outcomes_fixed), 						
												logit_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch 		= logit(beta_percent_bugs_reported_defined_outcomes_fixed_at_least_one_patch)); 	
    
profile_level_assigned_to_h4 	<<- profile_level_assigned_to_h4 %>%  
										mutate(	logit_percent_bugs_assigned_to_reopened_at_least_once						= logit(beta_percent_bugs_assigned_to_reopened_at_least_once),					
												logit_percent_bugs_assigned_to_reassigned_at_least_once						= logit(beta_percent_bugs_assigned_to_reassigned_at_least_once),					
												logit_percent_bugs_assigned_to_defined_outcomes_fixed 						= logit(beta_percent_bugs_assigned_to_defined_outcomes_fixed), 					
												logit_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch	= logit(beta_percent_bugs_assigned_to_defined_outcomes_fixed_at_least_one_patch));	
    
profile_level_qa_contact_h4 	<<- profile_level_qa_contact_h4 %>%
										mutate(	logit_percent_bugs_qa_contact_reopened_at_least_once						= logit(beta_percent_bugs_qa_contact_reopened_at_least_once),						
												logit_percent_bugs_qa_contact_reassigned_at_least_once						= logit(beta_percent_bugs_qa_contact_reassigned_at_least_once),					
												logit_percent_bugs_qa_contact_defined_outcomes_fixed 						= logit(beta_percent_bugs_qa_contact_defined_outcomes_fixed), 					
												logit_percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch		= logit(beta_percent_bugs_qa_contact_defined_outcomes_fixed_at_least_one_patch)); 	
																								
																								
												
												
												

# Now that the hypothesis specific contraints have been applied, we
# create subsets of each role where at least one bug was reopened for those DVs that depend on it

profile_level_reporter_reopened_h4			<<- filter(profile_level_reporter_h4, 		bugs_reported_at_least_one_reopened);
profile_level_assigned_to_reopened_h4		<<- filter(profile_level_assigned_to_h4, 	bugs_assigned_to_at_least_one_reopened);
profile_level_qa_contact_reopened_h4		<<- filter(profile_level_qa_contact_h4, 	bugs_qa_contact_at_least_one_reopened);


# We reepat for subsets where at least one bug was reassigned
profile_level_reporter_reassigned_h4		<<- filter(profile_level_reporter_h4, 		bugs_reported_at_least_one_reassigned);
profile_level_assigned_to_reassigned_h4		<<- filter(profile_level_assigned_to_h4, 	bugs_assigned_to_at_least_one_reassigned);
